package com.games.monaden.control;import com.games.monaden.services.audioPlayer.AudioPlayer;import java.io.File;/** * Created by Admin on 2016-05-21. */public class AudioController {    AudioPlayer musicPlayer;    public AudioController(){        musicPlayer = new AudioPlayer();    }    public void playMusic(int i){        musicPlayer.PlayMusic(i);    }    public void stopMusic(){        musicPlayer.stopMusic();    }    public void playSound(String filepath){        musicPlayer.PlaySound(filepath);    }    public double volumeDown(){        return musicPlayer.volumeDown();    }    public double volumeUp(){        return musicPlayer.volumeUp();    }}