package com.games.monaden.services.audioPlayer;import javafx.scene.media.Media;import javafx.scene.media.MediaPlayer;import javafx.util.Duration;import java.util.ArrayList;import java.util.List;/** * Created by Admin on 2016-05-18. */public class AudioPlayer {    private Media audioMusic;    private Media audioSound;    private MediaPlayer mediaplayerMusic;    private MediaPlayer mediaplayerSound;    private double volume = 0.8;    private int songNr = 0;    private List<String> musicList;    public AudioPlayer() {        musicList = new ArrayList<>();        loadMusicList();    }    public void stopMusic() {        this.mediaplayerMusic.stop();    }    public void PlayMusic(int songNr) {        try {            this.songNr = (Math.floorMod(this.songNr + songNr, musicList.size()));            String filepath = musicList.get(this.songNr);            this.audioMusic = new Media(filepath);            this.mediaplayerMusic = new MediaPlayer(this.audioMusic);            this.mediaplayerMusic.setOnEndOfMedia(new Runnable() {                public void run() {                    mediaplayerMusic.seek(Duration.ZERO);                }            });            this.mediaplayerMusic.setAutoPlay(true);            this.mediaplayerMusic.setVolume(volume);        } catch (Exception e) {            System.err.println("error: sound can't play");            System.err.println("error: " + e);        }    }    public void PlaySound(String filepath) {        try {            this.audioSound = new Media(filepath);            mediaplayerSound = new MediaPlayer(this.audioSound);            mediaplayerSound.setStopTime(javafx.util.Duration.ONE);            mediaplayerSound.play();        } catch (Exception e) {            System.err.println("error: sound can't play");        }    }    public double volumeDown() {        if (volume > 0.1) {            volume = volume - 0.1;            mediaplayerMusic.setVolume(volume);            System.out.println(mediaplayerMusic.getVolume());        }        return volume;    }    public double volumeUp() {        if (volume < 1) {            volume = volume + 0.1;            mediaplayerMusic.setVolume(volume);            System.out.println(mediaplayerMusic.getVolume());        }        return volume;    }    private void loadMusicList() {        musicList.add("file:/C:/Users/Admin/Documents/Git/Bravely-Segfault/src/main/resources/music/music.mp3");        musicList.add("file:/C:/Users/Admin/Documents/Git/Bravely-Segfault/src/main/resources/music/music2.mp3");    }}