package com.games.monaden.model;import com.games.monaden.model.primitives.Tile;import org.junit.Before;import org.junit.Test;import java.io.File;import static org.junit.Assert.assertTrue;/** * Created by Admin on 2016-07-07. */public class TileTest {    Tile testTile;    File file;    @Before    public void init(){        file = new File("src/main/resources/tiles/black.png");        testTile = new Tile(0, "black", true, file, false);    }    @Test    public void testId(){        assertTrue(testTile.getId() == 0);    }    @Test    public void testName(){        assertTrue(0 == testTile.getName().compareTo("black"));    }    @Test    public void testFilePath(){        assertTrue(testTile.getFilepath() == file);    }    @Test    public void testSolidess(){        assertTrue(testTile.isSolid());    }    @Test    public void testAnimated(){        assertTrue(!testTile.isAnimated());    }}